import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DeleteUser = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchUsers = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get('/api/users');
      setUsers(res.data.users || []);
    } catch (err) {
      setError('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    setError('');
    setSuccess('');
    try {
      await axios.delete(`/api/users/${id}`);
      setSuccess('User deleted successfully!');
      setUsers(users => users.filter(u => u._id !== id));
    } catch (err) {
      setError('Failed to delete user');
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-4">Delete User</h1>
      {loading ? (
        <div>Loading users...</div>
      ) : error ? (
        <div className="text-red-600 mb-4">{error}</div>
      ) : (
        <>
          {success && <div className="text-green-600 mb-4">{success}</div>}
          {users.length === 0 ? (
            <div className="text-gray-500">No users found.</div>
          ) : (
            <ul className="space-y-4">
              {users.map(user => (
                <li key={user._id} className="flex items-center justify-between border rounded p-3 bg-gray-50">
                  <div>
                    <div className="font-semibold text-gray-900">{user.name || user.firstName + ' ' + user.lastName}</div>
                    <div className="text-xs text-gray-500">{user.email} â€¢ {user.role}</div>
                  </div>
                  <button
                    className="px-4 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
                    onClick={() => handleDelete(user._id)}
                  >
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
};

export default DeleteUser;
