import React, { useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from 'recharts';
import { UserGroupIcon, DocumentTextIcon, ChatBubbleLeftRightIcon, ArrowTrendingUpIcon, PlusIcon, ArrowRightOnRectangleIcon, EyeIcon, PencilSquareIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../contexts/AuthContext';

const mockFeedbackCategories = [
  { name: 'Course Quality', count: 45 },
  { name: 'Instructor', count: 36 },
  { name: 'Materials', count: 42 },
  { name: 'Support', count: 33 },
  { name: 'Platform', count: 28 },
];

const mockRatingDistribution = [
  { name: '5 Stars', value: 120 },
  { name: '4 Stars', value: 89 },
  { name: '3 Stars', value: 45 },
  { name: '2 Stars', value: 20 },
  { name: '1 Star', value: 10 },
];

const mockStats = [
  {
    label: 'Total Forms',
    value: 12,
    icon: DocumentTextIcon,
    color: 'text-blue-600',
  },
  {
    label: 'Total Responses',
    value: 284,
    icon: ChatBubbleLeftRightIcon,
    color: 'text-green-600',
    highlight: true,
  },
  {
    label: 'Active Users',
    value: 67,
    icon: UserGroupIcon,
    color: 'text-indigo-600',
  },
  {
    label: 'Avg Rating',
    value: '4.2/5',
    icon: ArrowTrendingUpIcon,
    color: 'text-green-600',
    highlight: true,
  },
];

const mockAnalytics = [
  { name: 'Course Quality', rating: 4.5, responses: 45 },
  { name: 'Instructor', rating: 4.8, responses: 38 },
  { name: 'Materials', rating: 4.1, responses: 42 },
  { name: 'Support', rating: 4.3, responses: 35 },
  { name: 'Platform', rating: 3.9, responses: 29 },
];

const mockForms = [
  {
    title: 'Q4 Product Feedback',
    status: 'active',
    created: '2024-01-15',
    responses: 23,
  },
  {
    title: 'Course Evaluation',
    status: 'active',
    created: '2024-01-12',
    responses: 45,
  },
  {
    title: 'Service Assessment',
    status: 'completed',
    created: '2024-01-10',
    responses: 67,
  },
];

const COLORS = ['#00C49F', '#0088FE', '#FFBB28', '#FF8042', '#FF5C8A'];

const Dashboard = () => {
  const [tab, setTab] = useState('Overview');
  const { user } = useAuth();

  // Show mock data for demo admin accounts
  const isDemoAdmin = user && user.role === 'admin' && (
    user.email === 'demo' || user.email === 'demo@gmail.com' || user.email === 'admin@demo.com'
  );

  const feedbackCategories = isDemoAdmin ? mockFeedbackCategories : [];
  const ratingDistribution = isDemoAdmin ? mockRatingDistribution : [];
  const stats = isDemoAdmin ? mockStats : [];
  const analytics = isDemoAdmin ? mockAnalytics : [];
  const forms = isDemoAdmin ? mockForms : [];

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
        <div className="flex items-center gap-3">
          <div className="bg-blue-100 p-2 rounded-lg">
            <DocumentTextIcon className="h-8 w-8 text-blue-600" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Admin Dashboard</h1>
            <p className="text-gray-500 text-sm">Manage feedback forms and analyze responses</p>
          </div>
        </div>
        <div className="flex gap-2">
          <button className="flex items-center gap-1 px-4 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition">
            <PlusIcon className="h-5 w-5" />
            Create Form
          </button>
          <button className="flex items-center gap-1 px-4 py-2 border border-gray-300 rounded-lg font-medium text-gray-700 hover:bg-gray-100 transition">
            <ArrowRightOnRectangleIcon className="h-5 w-5" />
            Logout
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => (
          <div
            key={stat.label}
            className={`bg-white rounded-lg shadow-sm border border-gray-200 p-6 flex items-center gap-4 ${stat.highlight ? 'border-green-200' : ''}`}
          >
            <stat.icon className={`h-8 w-8 ${stat.color}`} />
            <div>
              <p className="text-sm font-medium text-gray-500">{stat.label}</p>
              <p className={`text-2xl font-semibold ${stat.highlight ? 'text-green-600' : 'text-gray-900'}`}>{stat.value}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Tabs */}
      <div className="flex gap-2 border-b border-gray-200">
        {['Overview', 'Analytics', 'Forms'].map((t) => (
          <button
            key={t}
            className={`px-4 py-2 font-medium ${tab === t ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
            onClick={() => setTab(t)}
          >
            {t}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      {tab === 'Overview' && (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Feedback Categories Bar Chart */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Feedback Categories</h2>
            <p className="text-gray-500 text-sm mb-2">Responses by category</p>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={feedbackCategories}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="count" fill="#3490dc" radius={[4, 4, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
          {/* Rating Distribution Pie Chart */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Rating Distribution</h2>
            <p className="text-gray-500 text-sm mb-2">Overall rating breakdown</p>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={ratingDistribution}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, value }) => `${name}: ${value}`}
                    outerRadius={90}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {ratingDistribution.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      )}
      {tab === 'Analytics' && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-lg font-semibold text-gray-900">Response Analytics</h2>
              <p className="text-gray-500 text-sm">Detailed feedback analysis</p>
            </div>
            <button className="flex items-center gap-1 px-4 py-2 border border-gray-300 rounded-lg font-medium text-gray-700 hover:bg-gray-100 transition">
              <ArrowTrendingUpIcon className="h-5 w-5" />
              Export Data
            </button>
          </div>
          <div className="space-y-4">
            {analytics.map((item) => (
              <div key={item.name} className="bg-gray-50 rounded-lg p-4 flex items-center justify-between">
                <div>
                  <div className="font-semibold text-gray-900">{item.name}</div>
                  <div className="text-sm text-gray-500">Rating: <span className="font-medium text-gray-900">{item.rating}/5</span></div>
                  <div className="w-40 h-2 bg-gray-200 rounded-full mt-2">
                    <div className="h-2 rounded-full bg-green-500" style={{ width: `${(item.rating / 5) * 100}%` }}></div>
                  </div>
                </div>
                <div className="text-sm text-gray-500">{item.responses} responses</div>
              </div>
            ))}
          </div>
        </div>
      )}
      {tab === 'Forms' && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Recent Feedback Forms</h2>
          <p className="text-gray-500 text-sm mb-4">Manage and monitor your feedback forms</p>
          <div className="space-y-4">
            {forms.map((form) => (
              <div key={form.title} className="flex items-center justify-between bg-gray-50 rounded-lg p-4 border border-gray-200">
                <div>
                  <div className="font-semibold text-gray-900 flex items-center gap-2">
                    {form.title}
                    <span className={`ml-2 px-2 py-0.5 rounded text-xs font-semibold ${form.status === 'active' ? 'bg-blue-100 text-blue-700' : 'bg-purple-100 text-purple-700'}`}>
                      {form.status}
                    </span>
                  </div>
                  <div className="text-xs text-gray-500 mt-1">Created: {form.created} • {form.responses} responses</div>
                </div>
                <div className="flex gap-2">
                  <button className="flex items-center gap-1 px-3 py-1 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-100 text-sm font-medium">
                    <EyeIcon className="h-4 w-4" /> View
                  </button>
                  <button className="flex items-center gap-1 px-3 py-1 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-100 text-sm font-medium">
                    <PencilSquareIcon className="h-4 w-4" /> Edit
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
