import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from 'recharts';
import { UserGroupIcon, DocumentTextIcon, ChatBubbleLeftRightIcon, ArrowTrendingUpIcon, PlusIcon, ArrowRightOnRectangleIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../contexts/AuthContext';
import { StatCard, AnalyticsCard, ResponseFormCard } from './DashboardStyles';
import ProfilePicture from '../common/ProfilePicture';

const mockFeedbackCategories = [
  { name: 'Course Quality', count: 45 },
  { name: 'Instructor', count: 36 },
  { name: 'Materials', count: 42 },
  { name: 'Support', count: 33 },
  { name: 'Platform', count: 28 },
];

const mockRatingDistribution = [
  { name: '5 Stars', value: 120 },
  { name: '4 Stars', value: 89 },
  { name: '3 Stars', value: 45 },
  { name: '2 Stars', value: 20 },
  { name: '1 Star', value: 10 },
];

const mockStats = [
  {
    label: 'Total Forms',
    value: 12,
    icon: DocumentTextIcon,
    color: 'text-blue-600',
  },
  {
    label: 'Total Responses',
    value: 284,
    icon: ChatBubbleLeftRightIcon,
    color: 'text-green-600',
    highlight: true,
  },
  {
    label: 'Active Users',
    value: 67,
    icon: UserGroupIcon,
    color: 'text-indigo-600',
  },
  {
    label: 'Avg Rating',
    value: '4.2/5',
    icon: ArrowTrendingUpIcon,
    color: 'text-green-600',
    highlight: true,
  },
];

const mockAnalytics = [
  { name: 'Course Quality', rating: 4.5, responses: 45 },
  { name: 'Instructor', rating: 4.8, responses: 38 },
  { name: 'Materials', rating: 4.1, responses: 42 },
  { name: 'Support', rating: 4.3, responses: 35 },
  { name: 'Platform', rating: 3.9, responses: 29 },
];

const mockForms = [
  {
    title: 'Q4 Product Feedback',
    status: 'active',
    created: '2024-01-15',
    responses: 23,
  },
  {
    title: 'Course Evaluation',
    status: 'active',
    created: '2024-01-12',
    responses: 45,
  },
  {
    title: 'Service Assessment',
    status: 'completed',
    created: '2024-01-10',
    responses: 67,
  },
];

const COLORS = ['#00C49F', '#0088FE', '#FFBB28', '#FF8042', '#FF5C8A'];

const Dashboard = () => {
  const [tab, setTab] = useState('Overview');
  const { user } = useAuth();

  // State for real data
  const [realStats, setRealStats] = useState([]);
  const [realFeedbackCategories, setRealFeedbackCategories] = useState([]);
  const [realRatingDistribution, setRealRatingDistribution] = useState([]);
  const [realAnalytics, setRealAnalytics] = useState([]);
  const [realForms, setRealForms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [globalLoading, setGlobalLoading] = useState(false); // NEW

  // Show mock data for demo admin accounts
  const isDemoAdmin = user && user.role === 'admin' && (
    user.email === 'demo' || user.email === 'demo@gmail.com' || user.email === 'admin@demo.com'
  );

  useEffect(() => {
    if (!isDemoAdmin && user) {
      setLoading(true);
      setGlobalLoading(true); // Show progress bar
      Promise.all([
        axios.get('/api/dashboard/stats'),
        axios.get('/api/dashboard/feedback-categories'),
        axios.get('/api/dashboard/rating-distribution'),
        axios.get('/api/dashboard/analytics'),
        axios.get('/api/dashboard/forms'),
      ])
        .then(([statsRes, catRes, ratingRes, analyticsRes, formsRes]) => {
          setRealStats(statsRes.data || []);
          setRealFeedbackCategories(catRes.data || []);
          setRealRatingDistribution(ratingRes.data || []);
          setRealAnalytics(analyticsRes.data || []);
          setRealForms(formsRes.data || []);
        })
        .catch(() => {
          // Optionally, fallback to mock data or show error
        })
        .finally(() => {
          setLoading(false);
          setGlobalLoading(false); // Hide progress bar
        });
    }
  }, [isDemoAdmin, user]);

  const feedbackCategories = isDemoAdmin ? mockFeedbackCategories : realFeedbackCategories;
  const ratingDistribution = isDemoAdmin ? mockRatingDistribution : realRatingDistribution;
  const stats = isDemoAdmin ? mockStats : realStats;
  const analytics = isDemoAdmin ? mockAnalytics : realAnalytics;
  const forms = isDemoAdmin ? mockForms : realForms;

  if (!isDemoAdmin && loading) {
    return <div className="flex justify-center items-center h-64 text-lg">Loading dashboard...</div>;
  }

  return (
    <div className="p-6 space-y-6">
      {globalLoading && (
        <div className="fixed top-0 left-0 w-full z-50">
          <div className="h-1 bg-blue-500 animate-pulse" style={{ width: '100%' }} />
          <div className="flex justify-center items-center py-2 bg-white shadow text-blue-600 font-semibold text-sm">Generating or loading data, please wait...</div>
        </div>
      )}
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
        <div className="flex items-center gap-4">
          <ProfilePicture user={user} size="xl" />
          <div>
            <h1 className="text-2xl font-bold text-gray-900">
              Welcome back, {user?.firstName}!
            </h1>
            <p className="text-gray-500 text-sm">
              {user?.role === 'admin' ? 'Admin Dashboard' : 'Employee Dashboard'} â€¢ {user?.department?.name || 'No Department'}
            </p>
          </div>
        </div>
        <div className="flex gap-2">
          <Link
            to="/surveys/create"
            className="flex items-center gap-1 px-4 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition"
          >
            <PlusIcon className="h-5 w-5" />
            Create Survey
          </Link>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => (
          <StatCard key={stat.label} {...stat} />
        ))}
      </div>

      {/* Tabs */}
      <div className="flex gap-2 border-b border-gray-200">
        {['Overview', 'Analytics', 'Forms'].map((t) => (
          <button
            key={t}
            className={`px-4 py-2 font-medium ${tab === t ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
            onClick={() => setTab(t)}
          >
            {t}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      {tab === 'Overview' && (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Feedback Categories Bar Chart */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Feedback Categories</h2>
            <p className="text-gray-500 text-sm mb-2">Responses by category</p>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={feedbackCategories}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="count" fill="#3490dc" radius={[4, 4, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
          {/* Rating Distribution Pie Chart */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Rating Distribution</h2>
            <p className="text-gray-500 text-sm mb-2">Overall rating breakdown</p>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={ratingDistribution}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, value }) => `${name}: ${value}`}
                    outerRadius={90}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {ratingDistribution.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      )}
      {tab === 'Analytics' && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-lg font-semibold text-gray-900">Response Analytics</h2>
              <p className="text-gray-500 text-sm">Detailed feedback analysis</p>
            </div>
            <button className="flex items-center gap-1 px-4 py-2 border border-gray-300 rounded-lg font-medium text-gray-700 hover:bg-gray-100 transition">
              <ArrowTrendingUpIcon className="h-5 w-5" />
              Export Data
            </button>
          </div>
          <div className="space-y-4">
            {analytics.map((item) => (
              <AnalyticsCard key={item.name} {...item} />
            ))}
          </div>
        </div>
      )}
      {tab === 'Forms' && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Recent Feedback Forms</h2>
          <p className="text-gray-500 text-sm mb-4">Manage and monitor your feedback forms</p>
          <div className="space-y-4">
            {forms.map((form) => (
              <ResponseFormCard key={form.title} {...form} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
