import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DeleteSurvey = () => {
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchSurveys = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get('/api/surveys');
      setSurveys(res.data.surveys || []);
    } catch (err) {
      setError('Failed to fetch surveys');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSurveys();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this survey?')) return;
    setError('');
    setSuccess('');
    try {
      await axios.delete(`/api/surveys/${id}`);
      setSuccess('Survey deleted successfully!');
      setSurveys(surveys => surveys.filter(s => s._id !== id));
    } catch (err) {
      setError('Failed to delete survey');
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-4">Delete Survey</h1>
      {loading ? (
        <div>Loading surveys...</div>
      ) : error ? (
        <div className="text-red-600 mb-4">{error}</div>
      ) : (
        <>
          {success && <div className="text-green-600 mb-4">{success}</div>}
          {surveys.length === 0 ? (
            <div className="text-gray-500">No surveys found.</div>
          ) : (
            <ul className="space-y-4">
              {surveys.map(survey => (
                <li key={survey._id} className="flex items-center justify-between border rounded p-3 bg-gray-50">
                  <div>
                    <div className="font-semibold text-gray-900">{survey.title}</div>
                    <div className="text-xs text-gray-500">{survey.description}</div>
                  </div>
                  <button
                    className="px-4 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
                    onClick={() => handleDelete(survey._id)}
                  >
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
};

export default DeleteSurvey;
