import React from 'react';

const ProfilePicture = ({ 
  user, 
  size = 'md', 
  showName = false, 
  className = '',
  onClick = null,
  showOnlineStatus = false 
}) => {
  const sizeClasses = {
    xs: 'h-6 w-6 text-xs',
    sm: 'h-8 w-8 text-sm',
    md: 'h-10 w-10 text-base',
    lg: 'h-12 w-12 text-lg',
    xl: 'h-16 w-16 text-xl',
    '2xl': 'h-20 w-20 text-2xl',
    '3xl': 'h-24 w-24 text-3xl'
  };

  const getInitials = (user) => {
    if (!user) return '?';
    const firstName = user.firstName || '';
    const lastName = user.lastName || '';
    const initials = `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
    return initials || user.email?.charAt(0).toUpperCase() || '?';
  };

  const getDisplayName = (user) => {
    if (!user) return 'Unknown User';
    return `${user.firstName || ''} ${user.lastName || ''}`.trim() || user.email || 'User';
  };

  const getBgColor = (user) => {
    if (!user) return 'bg-gray-500';

    const colors = [
      'bg-red-500', 'bg-blue-500', 'bg-green-500', 'bg-yellow-500',
      'bg-purple-500', 'bg-pink-500', 'bg-indigo-500', 'bg-teal-500',
      'bg-orange-500', 'bg-cyan-500', 'bg-lime-500', 'bg-emerald-500'
    ];

    const name = getDisplayName(user);
    const index = name.charCodeAt(0) % colors.length;
    return colors[index];
  };

  const profileElement = (
    <div 
      className={`relative inline-flex items-center justify-center rounded-full ${sizeClasses[size]} ${className} ${
        onClick ? 'cursor-pointer hover:opacity-80 transition-opacity' : ''
      }`}
      onClick={onClick}
    >
      {user?.profilePicture ? (
        <img
          src={user.profilePicture}
          alt={getDisplayName(user)}
          className={`rounded-full object-cover ${sizeClasses[size]} ring-2 ring-white shadow-sm`}
        />
      ) : (
        <div className={`${getBgColor(user)} text-white font-semibold rounded-full flex items-center justify-center ${sizeClasses[size]} ring-2 ring-white shadow-sm`}>
          {getInitials(user)}
        </div>
      )}

      {/* Online status indicator */}
      {showOnlineStatus && user?.isOnline && (
        <div className="absolute bottom-0 right-0 h-3 w-3 bg-green-400 border-2 border-white rounded-full"></div>
      )}
    </div>
  );

  if (showName) {
    return (
      <div className="flex items-center space-x-3">
        {profileElement}
        <div className="min-w-0 flex-1">
          <p className="text-sm font-medium text-gray-900 truncate">
            {getDisplayName(user)}
          </p>
          {user?.position && (
            <p className="text-xs text-gray-500 truncate">
              {user.position}
            </p>
          )}
          {user?.department && (
            <p className="text-xs text-gray-400 truncate">
              {user.department.name}
            </p>
          )}
        </div>
      </div>
    );
  }

  return profileElement;
};

export default ProfilePicture;
